// --- DOM Elements ---
const mainMenuContainer = document.getElementById('main-menu-container');
const sectionButtonsGrid = document.getElementById('section-buttons-grid');
const quizBodyContainer = document.getElementById('quiz-body-container');
const quizFooter = document.getElementById('quiz-footer');
const sectionResultsContainer = document.getElementById('section-results-container');
const questionTextEl = document.getElementById('question-text');
const optionsListEl = document.getElementById('options-list');
const nextBtn = document.getElementById('next-btn');
const progressTextEl = document.getElementById('progress-text');
const scoreTextEl = document.getElementById('score-text');
const percentageTextEl = document.getElementById('percentage-text');
const retrySectionBtn = document.getElementById('retry-section-btn');
const backToMenuBtn = document.getElementById('back-to-menu-btn');
const menuFromQuizBtn = document.getElementById('menu-from-quiz-btn');
const headerSubtitle = document.getElementById('header-subtitle');
const resultsTitle = document.getElementById('results-title');

let userAnswers = {};
let sectionScores = {};
let currentSectionKey = null;
let currentQuestionIndex = 0;


function initializeState() {
    const sectionKeys = Object.keys(questionsBySection);
    sectionKeys.forEach(key => {
        // Shuffle questions at the start
        questionsBySection[key].sort(() => Math.random() - 0.5);
        // Initialize answer and score objects if they don't exist
        if (!userAnswers[key]) {
            userAnswers[key] = Array(questionsBySection[key].length).fill(null);
        }
        if (sectionScores[key] === undefined) {
            sectionScores[key] = null;
        }
    });
}

function renderMainMenu() {
    // Show/Hide relevant containers
    mainMenuContainer.style.display = 'block';
    quizBodyContainer.style.display = 'none';
    quizFooter.style.display = 'none';
    sectionResultsContainer.style.display = 'none';
    headerSubtitle.textContent = 'अपनी पसंद का एक अनुभाग चुनें!';

    // Clear previous buttons and render new ones
    sectionButtonsGrid.innerHTML = '';
    Object.keys(questionsBySection).forEach(key => {
        const button = document.createElement('button');
        button.dataset.key = key;
        button.classList.add('section-btn', 'w-full', 'text-left', 'p-5', 'bg-gray-100', 'rounded-lg', 'border-2', 'hover:bg-gray-200', 'hover:border-blue-300', 'transition-all', 'duration-200', 'flex', 'justify-between', 'items-center');

        const score = sectionScores[key];
        const total = questionsBySection[key].length;

        let scoreDisplay = score !== null ? `स्कोर: ${score}/${total}` : `${total} प्रश्न`;
        button.innerHTML = `<span class="font-semibold text-lg">${key}</span> <span class="text-sm text-gray-600">${scoreDisplay}</span>`;

        if (score !== null) {
            button.classList.add('completed');
            button.querySelector('.text-gray-600').classList.add('text-blue-200');
        }

        button.addEventListener('click', () => startSectionPoll(key));
        sectionButtonsGrid.appendChild(button);
    });
}

function startSectionPoll(sectionKey) {
    currentSectionKey = sectionKey;
    currentQuestionIndex = 0;
    userAnswers[currentSectionKey] = Array(questionsBySection[currentSectionKey].length).fill(null);

    mainMenuContainer.style.display = 'none';
    quizBodyContainer.style.display = 'block';
    quizFooter.style.display = 'flex';
    headerSubtitle.textContent = `अनुभाग: ${currentSectionKey}`;

    loadQuestion();
}

function loadQuestion() {
    resetOptions();
    const sectionQuestions = questionsBySection[currentSectionKey];
    const question = sectionQuestions[currentQuestionIndex];

    questionTextEl.textContent = question.question;

    question.options.forEach((option, index) => {
        const button = document.createElement('button');
        button.textContent = option;
        button.classList.add('option-btn', 'w-full', 'text-left', 'p-4', 'bg-gray-100', 'rounded-lg', 'border-2', 'border-transparent', 'hover:bg-gray-200', 'hover:border-blue-300', 'transition-all', 'duration-200');
        button.dataset.index = index;

        const userAnswer = userAnswers[currentSectionKey][currentQuestionIndex];
        if (userAnswer !== null) {
            button.classList.add('answered');
            if (index === question.answer) button.classList.add('correct');
            else if (index === userAnswer) button.classList.add('incorrect');
            button.disabled = true;
        } else {
            button.addEventListener('click', selectAnswer);
        }
        optionsListEl.appendChild(button);
    });
    updateQuizUI();
}

function selectAnswer(e) {
    const selectedIndex = parseInt(e.target.dataset.index);
    const question = questionsBySection[currentSectionKey][currentQuestionIndex];
    userAnswers[currentSectionKey][currentQuestionIndex] = selectedIndex;

    Array.from(optionsListEl.children).forEach((button, index) => {
        if (index === question.answer) button.classList.add('correct');
        else if (index === selectedIndex) button.classList.add('incorrect');
        button.disabled = true;
        button.classList.add('answered');
    });
    updateQuizUI();
}

function updateQuizUI() {
    const sectionQuestions = questionsBySection[currentSectionKey];
    progressTextEl.textContent = `प्रश्न ${currentQuestionIndex + 1} / ${sectionQuestions.length}`;

    const userAnswer = userAnswers[currentSectionKey][currentQuestionIndex];
    nextBtn.style.display = userAnswer !== null ? 'block' : 'none';

    if (currentQuestionIndex === sectionQuestions.length - 1) {
        nextBtn.textContent = 'परिणाम देखें';
    } else {
        nextBtn.textContent = 'अगला';
    }
}

function showSectionResults() {
    const sectionQuestions = questionsBySection[currentSectionKey];
    const answers = userAnswers[currentSectionKey];
    let score = 0;
    answers.forEach((answer, index) => {
        if (answer === sectionQuestions[index].answer) score++;
    });

    sectionScores[currentSectionKey] = score; // Save the score

    quizBodyContainer.style.display = 'none';
    quizFooter.style.display = 'none';
    sectionResultsContainer.style.display = 'block';

    resultsTitle.textContent = `'${currentSectionKey}' अनुभाग पूरा हुआ!`;
    const total = sectionQuestions.length;
    const percentage = total > 0 ? ((score / total) * 100).toFixed(2) : 0;

    scoreTextEl.textContent = `आपने ${total} में से ${score} अंक प्राप्त किए।`;
    percentageTextEl.textContent = `प्रतिशत: ${percentage}%`;
}

function resetOptions() {
    optionsListEl.innerHTML = '';
}

nextBtn.addEventListener('click', () => {
    const sectionQuestions = questionsBySection[currentSectionKey];
    if (currentQuestionIndex < sectionQuestions.length - 1) {
        currentQuestionIndex++;
        loadQuestion();
    } else {
        showSectionResults();
    }
});

retrySectionBtn.addEventListener('click', () => {
    sectionResultsContainer.style.display = 'none';
    startSectionPoll(currentSectionKey);
});

backToMenuBtn.addEventListener('click', renderMainMenu);
menuFromQuizBtn.addEventListener('click', renderMainMenu);

document.addEventListener('DOMContentLoaded', () => {
    initializeState();
    renderMainMenu();
});
